import React from "react";
import Input from "../Input/Input";
// import Drop from '../Drop/Drop'
import Btn from "../Btn/Btn";
import "./basic.css";
import { useFormik } from "formik";
const BasicDetail = () => {
  const formik = useFormik({
    initialValues: {
      Name: "",
      email: "",
      pan: "",
      aadhar: "",
      afhmn: "",
      mn: "",
    },
  });
  console.log(formik.values);
  return (
    <div>
      <div className="basic_details">
        <h4>Basic details</h4>
        <Input label="Name" name="Name" value={formik.values.Name}  onChange={formik.handleChange}/>
        <em>*Name as per PAN</em>
        <Input label="Email" name="email" value={formik.values.email}  onChange={formik.handleChange}/>
        {/* <Drop label=' Gender' opt1='Male' opt2='Female' opt3='Others'/>
            <Drop label='Marital Status' opt1='Single' opt2='Married' opt3='Divorced'/> */}
        <Input label="Pan" name="pan" value={formik.values.pan}  onChange={formik.handleChange}/>
        <Input label="Aadhar" name="aadhar" value={formik.values.aadhar}  onChange={formik.handleChange}/>
        <Input label="Applicant's Father/Husband Name" name="afhn" value={formik.values.afhn}  onChange={formik.handleChange}/>
        <Input label="Mother's Name" name="mn" value={formik.values.mn}  onChange={formik.handleChange}/>
      </div>
      <hr />
      <div className="btn">
        <Btn label="back" />
        <Btn label="Next" />
      </div>
    </div>
  );
};

export default BasicDetail;
