import React from "react";
import Input from "../Input/Input";
// import Drop from '../Drop/Drop'
import Btn from "../Btn/Btn";
import "./basic.css";
import { useFormik } from "formik";
const BasicDetail = () => {
  const formik = useFormik({
    initialValues: {
      Name: "",
      email: "",
      pan: "",
      aadhar: "",
      afhmn: "",
      mn: "",
    },
  });
  console.log(formik.values);
  return (
    <div>
      <form>
        <div className="basic_details">
          <h4>Basic details</h4>
          <Input
            label="Name"
            name="Name"
            value={formik.values.Name}
            handleChange={formik.handleChange}
          />
          <em>*Name as per PAN</em>
          <Input
            label="Email"
            name="email"
            value={formik.values.email}
            handleChange={formik.handleChange}
          />
          {/* <Drop label=' Gender' opt1='Male' opt2='Female' opt3='Others'/>
            <Drop label='Marital Status' opt1='Single' opt2='Married' opt3='Divorced'/> */}
          <Input
            label="Pan"
            name="pan"
            value={formik.values.pan}
            handleChange={formik.handleChange}
          />
          <Input
            label="Aadhar"
            name="aadhar"
            value={formik.values.aadhar}
            handleChange={formik.handleChange}
          />
          <Input
            label="Applicant's Father/Husband Name"
            name="afhn"
            value={formik.values.afhn}
            handleChange={formik.handleChange}
          />
          <Input
            label="Mother's Name"
            name="mn"
            value={formik.values.mn}
            handle={formik.handleChange}
          />
        </div>
        <hr />
        <div className="btn">
          <Btn label="Back" />
          <Btn label="Next" />
        </div>
      </form>
    </div>
  );
};

export default BasicDetail;
